import './App.css';

function App() {
  const handleClick = ()=>{
    alert("button is clicked!")
  }
  return (
    <>
    <div className="btn">
      <button onClick = {handleClick}>Click</button>

      {/* special case : input (in this we have to do onchange and there is a role of state as well) */}
      <input type="text" />
    </div>
    </>
  );
}

export default App;
